let video=document.getElementById("video__media"),video_image=document.querySelector(".video__image"),paused=!0,video_button=document.getElementById("video__button--parent");video_button.addEventListener("click",()=>{paused?(video.play(),paused=!1,video_image.classList.toggle("video__image--toggle")):(video.pause(),paused=!0,video_image.classList.toggle("video__image--toggle"))});